    //MARK: -
    private func countryInformation(countryCode:String) -> AppCountryModel{
        
        var flag: String? = ""
        let flagBaseCode = UnicodeScalar("ðŸ‡¦").value - UnicodeScalar("A").value
        countryCode.uppercased().unicodeScalars.forEach {
            if let scaler = UnicodeScalar(flagBaseCode + $0.value) {
                flag?.append(String(describing: scaler))
            }
        }
        if flag?.count != 1 {
            flag = nil
        }
        
        let locale = Locale(identifier: "en_US")
        let countryName = locale.localizedString(forRegionCode: countryCode)
        let phoneCode = GetCountryCallingCode(countryRegionCode: countryCode) ?? ""
        
//        print(countryName ?? "No name")
//        print(flag ?? "No flag")
//        print(countryCode)
//        print(phoneCode)
//
        return AppCountryModel(name: countryName ?? "United States", flag: flag ?? "ðŸ‡ºðŸ‡¸", code: countryCode, phoneCode: phoneCode)
        
    }
    
