    func getCountries() -> [AppCountryModel]{
        
        var countries = [AppCountryModel]()
        for item in flags{
            let country = self.countryInformation(countryCode: item.key)
            if country.phoneCode != ""{
                countries.append(country)
            }
        }
        
        countries = countries.sorted { $0.name < $1.name }
        
        return countries
    }
    
    func getCountryByCode(phoneCode:String) -> AppCountryModel? {
        return getCountries().filter({$0.code == phoneCode}).first
    }
